import { Fragment, useCallback, useEffect, useMemo, useState } from "react"
import { InputSelect } from "./components/InputSelect"
import { Instructions } from "./components/Instructions"
import { Transactions } from "./components/Transactions"
import { useEmployees } from "./hooks/useEmployees"
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions"
import { useTransactionsByEmployee } from "./hooks/useTransactionsByEmployee"
import { EMPTY_EMPLOYEE } from "./utils/constants"
import { Employee } from "./utils/types"

export function App() {
  const { data: employees, ...employeeUtils } = useEmployees()
  const { data: paginatedTransactions, ...paginatedTransactionsUtils } = usePaginatedTransactions()
  const { data: transactionsByEmployee, ...transactionsByEmployeeUtils } = useTransactionsByEmployee()

  const [isLoading, setIsLoading] = useState(false)
  const [isAllEmployees, setIsAllEmployees] = useState(true)
  const [hasMoreTransactions, setHasMoreTransactions] = useState(true)

  // Log the structure of paginatedTransactions for debugging
  useEffect(() => {
    console.log("Paginated Transactions:", paginatedTransactions)
  }, [paginatedTransactions])

  const transactions = useMemo(() => {
    if (isAllEmployees) {
      return paginatedTransactions?.data || []
    }
    return transactionsByEmployee || []
  }, [paginatedTransactions, transactionsByEmployee, isAllEmployees])

  const loadAllTransactions = useCallback(async () => {
    setIsLoading(true)
    setIsAllEmployees(true)
    transactionsByEmployeeUtils.invalidateData()

    console.log("Fetching all transactions...")

    await employeeUtils.fetchAll() // Ensure employees are fetched
    await paginatedTransactionsUtils.fetchAll() // Fetch paginated transactions

    setIsLoading(false) // Stop loading state
  }, [employeeUtils, paginatedTransactionsUtils, transactionsByEmployeeUtils])

  const loadTransactionsByEmployee = useCallback(
    async (employeeId: string) => {
      if (!employeeId) return // Prevent empty employeeId
      setIsLoading(true) // Set loading state to true
      setIsAllEmployees(false) // Set to "Specific Employee" mode
      paginatedTransactionsUtils.invalidateData() // Invalidate previous transactions

      console.log("Fetching transactions for employee ID:", employeeId)
      await transactionsByEmployeeUtils.fetchById(employeeId) // Fetch transactions for the specific employee
      setIsLoading(false) // Reset loading state after fetching
    },
    [paginatedTransactionsUtils, transactionsByEmployeeUtils]
  )

  useEffect(() => {
    if (employees === null && !employeeUtils.loading) {
      loadAllTransactions()
    }
  }, [employeeUtils.loading, employees, loadAllTransactions])

  const handleViewMore = async () => {
    if (isAllEmployees && hasMoreTransactions) {
      console.log("Loading more transactions...")
      await paginatedTransactionsUtils.fetchAll() // Ensure this triggers the fetch
      setHasMoreTransactions(paginatedTransactionsUtils.data?.nextPage !== null) // Check if more transactions are available
    } else {
      console.warn("No more transactions to load.")
    }
  }

  return (
    <Fragment>
      <main className="MainContainer">
        <Instructions />
        <hr className="RampBreak--l" />

        <InputSelect<Employee>
          isLoading={isLoading}
          defaultValue={EMPTY_EMPLOYEE}
          items={employees === null ? [] : [EMPTY_EMPLOYEE, ...employees]}
          label="Filter by employee"
          loadingLabel="Loading employees"
          parseItem={(item) => ({
            value: item.id,
            label: `${item.firstName} ${item.lastName}`,
          })}
          onChange={async (newValue) => {
            console.log("Selected employee:", newValue)
            if (newValue === null || newValue.id === "all-employees") {
              console.log("Switching to All Employees...")
              await loadAllTransactions() // Load all transactions when switching back to all employees
            } else if (newValue.id) {
              console.log("Loading transactions for specific employee:", newValue.id)
              await loadTransactionsByEmployee(newValue.id) // Load specific employee's transactions
            }
          }}
        />

        <div className="RampBreak--l" />

        <div className="RampGrid">
          {isLoading ? <div>Loading transactions...</div> : <Transactions transactions={transactions} />}
          {isAllEmployees && hasMoreTransactions && (
            <button
              className="RampButton"
              disabled={paginatedTransactionsUtils.loading}
              onClick={handleViewMore}
            >
              View More
            </button>
          )}
        </div>
      </main>
    </Fragment>
  )
}
