import { useEffect } from "react";
import { useEmployees } from "./hooks/useEmployees";
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions";
import { Employee } from "./utils/types"; // Ensure you import Employee type

export function App() {
  const { data: employees, fetchAll: fetchEmployees } = useEmployees();
  const { data: paginatedTransactions, fetchAll: fetchPaginatedTransactions, loading } = usePaginatedTransactions();

  // Fetch employees on mount
  useEffect(() => {
    if (!employees) {
      fetchEmployees();
    }
  }, [employees, fetchEmployees]);

  // Fetch all transactions when "All Employees" is selected
  const handleEmployeeChange = async (selectedEmployee: Employee | null) => { // Define the type explicitly
    if (!selectedEmployee || selectedEmployee.id === "all-employees") {
      // Fetch transactions for all employees
      await fetchPaginatedTransactions();
    } else {
      // Fetch transactions for the selected employee
      console.log(`Fetching transactions for employee ID: ${selectedEmployee.id}`);
      // Call the function to fetch transactions for this employee
      // You may need to implement a separate function to fetch transactions by employee
    }
  };

  return (
    <div>
      {/* Your dropdown component here, passing in handleEmployeeChange */}
      {/* Example usage of loading and paginatedTransactions */}
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div>
          {paginatedTransactions && paginatedTransactions.data.map(transaction => (
            <div key={transaction.id}>
              {/* Render your transaction details here */}
              {transaction.merchant} - {transaction.amount}
            </div>
          ))}
        </div>
      )}
      {/* Your input select for employee filtering */}
      {/* <InputSelect
          onChange={handleEmployeeChange}
          items={employees}
          ...otherProps
      /> */}
    </div>
  );
}
