import { Fragment, useCallback, useEffect, useState } from "react"
import { InputSelect } from "./components/InputSelect"
import { Instructions } from "./components/Instructions"
import { Transactions } from "./components/Transactions"
import { useEmployees } from "./hooks/useEmployees"
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions"
import { useTransactionsByEmployee } from "./hooks/useTransactionsByEmployee"
import { EMPTY_EMPLOYEE } from "./utils/constants"
import { Employee } from "./utils/types"

export function App() {
  const { data: employees, fetchAll: fetchEmployees } = useEmployees()
  const { data: paginatedTransactions, fetchAll: fetchPaginatedTransactions } =
    usePaginatedTransactions()
  const { data: transactionsByEmployee, fetchById: fetchTransactionsByEmployee } =
    useTransactionsByEmployee()

  const [isLoading, setIsLoading] = useState(false)
  const [selectedEmployee, setSelectedEmployee] = useState<Employee | null>(null)

  // Fetch employees and all transactions on initial load
  useEffect(() => {
    const loadData = async () => {
      setIsLoading(true)
      await fetchEmployees()
      await fetchPaginatedTransactions()
      setIsLoading(false)
    }

    loadData()
  }, [fetchEmployees, fetchPaginatedTransactions])

  // Function to handle employee selection
  const handleEmployeeChange = async (newValue: Employee | null) => {
    console.log("Selected employee:", newValue) // Log the selected employee
    setSelectedEmployee(newValue)
    setIsLoading(true) // Start loading indicator

    if (!newValue || newValue.id === "all-employees") {
      console.log("Loading transactions for all employees...") // Log action
      await fetchPaginatedTransactions() // Load all transactions for all employees
    } else {
      console.log(`Loading transactions for employee ID: ${newValue.id}`) // Log action
      await fetchTransactionsByEmployee(newValue.id) // Load transactions for selected employee
    }

    setIsLoading(false) // Stop loading indicator
  }

  const transactions =
    selectedEmployee && selectedEmployee.id !== "all-employees"
      ? transactionsByEmployee
      : paginatedTransactions?.data || []

  return (
    <Fragment>
      <main className="MainContainer">
        <Instructions />
        <hr className="RampBreak--l" />

        <InputSelect<Employee>
          isLoading={isLoading}
          defaultValue={EMPTY_EMPLOYEE}
          items={employees ? [EMPTY_EMPLOYEE, ...employees] : []}
          label="Filter by employee"
          loadingLabel="Loading employees..."
          parseItem={(item) => ({
            value: item.id,
            label: `${item.firstName} ${item.lastName}`,
          })}
          onChange={handleEmployeeChange}
        />

        <div className="RampBreak--l" />

        <div className="RampGrid">
          {isLoading ? <div>Loading transactions...</div> : <Transactions transactions={transactions} />}

          {selectedEmployee?.id === "all-employees" && paginatedTransactions?.nextPage && (
            <button
              className="RampButton"
              onClick={fetchPaginatedTransactions} // Load more transactions
              disabled={isLoading}
            >
              View More
            </button>
          )}
        </div>
      </main>
    </Fragment>
  )
}
