import { Fragment, useCallback, useEffect, useMemo, useState } from "react"
import { InputSelect } from "./components/InputSelect"
import { Instructions } from "./components/Instructions"
import { Transactions } from "./components/Transactions"
import { useEmployees } from "./hooks/useEmployees"
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions"
import { useTransactionsByEmployee } from "./hooks/useTransactionsByEmployee"
import { EMPTY_EMPLOYEE } from "./utils/constants"
import { Employee } from "./utils/types"

export function App() {
  const { data: employees, fetchAll: fetchEmployees } = useEmployees()
  const {
    data: paginatedTransactions,
    fetchAll: fetchPaginatedTransactions,
    loading: paginatedLoading,
  } = usePaginatedTransactions()
  const {
    data: transactionsByEmployee,
    fetchById: fetchTransactionsByEmployee,
    invalidateData: invalidateEmployeeData,
  } = useTransactionsByEmployee()

  const [isLoading, setIsLoading] = useState(false)
  const [isAllEmployees, setIsAllEmployees] = useState(true)
  const [selectedEmployee, setSelectedEmployee] = useState<Employee | null>(EMPTY_EMPLOYEE)

  // Log current states for debugging
  useEffect(() => {
    console.log("Paginated Transactions:", paginatedTransactions)
    console.log("Transactions by Employee:", transactionsByEmployee)
    console.log("isAllEmployees:", isAllEmployees)
    console.log("Selected Employee:", selectedEmployee)
  }, [paginatedTransactions, transactionsByEmployee, isAllEmployees, selectedEmployee])

  // Memoized transaction data to display
  const transactions = useMemo(() => {
    if (isAllEmployees) {
      return paginatedTransactions?.data || []
    }
    return transactionsByEmployee || []
  }, [paginatedTransactions, transactionsByEmployee, isAllEmployees])

  // Load all transactions when "All Employees" is selected
  const loadAllTransactions = useCallback(async () => {
    setIsLoading(true)
    setIsAllEmployees(true)
    console.log("Fetching all transactions...")

    await fetchEmployees()
    await fetchPaginatedTransactions()
    invalidateEmployeeData() // Ensure employee-specific data is cleared
    setIsLoading(false)
  }, [fetchEmployees, fetchPaginatedTransactions, invalidateEmployeeData])

  // Load transactions for specific employee
  const loadTransactionsByEmployee = useCallback(
    async (employeeId: string) => {
      if (!employeeId) return // Prevent empty employeeId
      setIsLoading(true)
      setIsAllEmployees(false) // Set to "Specific Employee" mode

      console.log("Fetching transactions for employee ID:", employeeId)
      await fetchTransactionsByEmployee(employeeId)
      const selectedEmp = employees?.find((emp) => emp.id === employeeId)
      setSelectedEmployee(selectedEmp || EMPTY_EMPLOYEE) // Set selected employee
      setIsLoading(false)
    },
    [fetchTransactionsByEmployee, employees]
  )

  // Monitor changes in `isAllEmployees` to load all transactions correctly
  useEffect(() => {
    if (isAllEmployees) {
      loadAllTransactions()
    }
  }, [isAllEmployees, loadAllTransactions])

  // Handle employee change from dropdown
  const handleEmployeeChange = async (newValue: Employee | null) => {
    console.log("Selected employee:", newValue)

    if (!newValue || newValue.id === "all-employees") {
      console.log("Switching to All Employees...")

      // Set the states and let the `useEffect` hook take care of loading transactions
      setIsAllEmployees(true)
      setSelectedEmployee(EMPTY_EMPLOYEE)
      return
    }

    if (newValue.id) {
      console.log("Loading transactions for specific employee:", newValue.id)

      // Load specific employee's transactions
      await loadTransactionsByEmployee(newValue.id)
    }
  }

  return (
    <Fragment>
      <main className="MainContainer">
        <Instructions />
        <hr className="RampBreak--l" />

        <InputSelect<Employee>
          isLoading={isLoading}
          defaultValue={EMPTY_EMPLOYEE}
          items={employees === null ? [] : [EMPTY_EMPLOYEE, ...employees]}
          label="Filter by employee"
          loadingLabel="Loading employees"
          parseItem={(item) => ({
            value: item.id,
            label: `${item.firstName} ${item.lastName}`,
          })}
          onChange={handleEmployeeChange} // Using the separate handler
        />

        <div className="RampBreak--l" />

        <div className="RampGrid">
          {isLoading ? <div>Loading transactions...</div> : <Transactions transactions={transactions} />}

          {isAllEmployees && (
            <button
              className="RampButton"
              disabled={isLoading || paginatedLoading}
              onClick={handleViewMore}
            >
              View More
            </button>
          )}
        </div>
      </main>
    </Fragment>
  )
}
