import { Fragment, useCallback, useEffect, useMemo, useState } from "react"
import { InputSelect } from "./components/InputSelect"
import { Instructions } from "./components/Instructions"
import { Transactions } from "./components/Transactions"
import { useEmployees } from "./hooks/useEmployees"
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions"
import { useTransactionsByEmployee } from "./hooks/useTransactionsByEmployee"
import { EMPTY_EMPLOYEE } from "./utils/constants"
import { Employee } from "./utils/types"

export function App() {
  const { data: employees, ...employeeUtils } = useEmployees()
  const { data: paginatedTransactions, ...paginatedTransactionsUtils } = usePaginatedTransactions()
  const { data: transactionsByEmployee, ...transactionsByEmployeeUtils } = useTransactionsByEmployee()

  const [isLoading, setIsLoading] = useState(false)
  const [isAllEmployees, setIsAllEmployees] = useState(true)

  const transactions = useMemo(() => {
    return isAllEmployees ? paginatedTransactions?.data || [] : transactionsByEmployee || []
  }, [isAllEmployees, paginatedTransactions, transactionsByEmployee])

  const loadAllTransactions = useCallback(async () => {
    setIsLoading(true)
    await employeeUtils.fetchAll() // Fetch employees
    await paginatedTransactionsUtils.fetchAll() // Fetch all transactions
    setIsLoading(false)
  }, [employeeUtils, paginatedTransactionsUtils])

  const loadTransactionsByEmployee = useCallback(
    async (employeeId: string) => {
      await transactionsByEmployeeUtils.fetchById(employeeId)
    },
    [transactionsByEmployeeUtils]
  )

  useEffect(() => {
    const initialize = async () => {
      if (!employees && !employeeUtils.loading) {
        await loadAllTransactions()
      }
    }
    initialize()
  }, [employees, employeeUtils.loading, loadAllTransactions])

  const handleEmployeeChange = useCallback(
    async (newValue: Employee | null) => {
      setIsLoading(true)
      if (!newValue || newValue.id === "all-employees") {
        console.log("Switching to All Employees.")
        setIsAllEmployees(true)
        await loadAllTransactions() // Load all transactions
      } else {
        console.log(`Fetching transactions for employee ID: ${newValue.id}`)
        setIsAllEmployees(false)
        await loadTransactionsByEmployee(newValue.id) // Fetch transactions for specific employee
      }
      setIsLoading(false)
    },
    [loadAllTransactions, loadTransactionsByEmployee]
  )

  return (
    <Fragment>
      <main className="MainContainer">
        <Instructions />
        <hr className="RampBreak--l" />

        <InputSelect<Employee>
          isLoading={isLoading}
          defaultValue={EMPTY_EMPLOYEE}
          items={employees ? [EMPTY_EMPLOYEE, ...employees] : []}
          label="Filter by employee"
          loadingLabel="Loading employees..."
          parseItem={(item) => ({
            value: item.id,
            label: `${item.firstName} ${item.lastName}`,
          })}
          onChange={handleEmployeeChange} // Handle employee selection
        />

        <div className="RampBreak--l" />

        <div className="RampGrid">
          {isLoading ? <div>Loading transactions...</div> : <Transactions transactions={transactions} />}
          {isAllEmployees && paginatedTransactions?.nextPage && (
            <button
              className="RampButton"
              onClick={async () => {
                await paginatedTransactionsUtils.fetchAll() // Load more transactions
              }}
              disabled={isLoading || paginatedTransactionsUtils.loading}
            >
              View More
            </button>
          )}
        </div>
      </main>
    </Fragment>
  )
}
