import { Fragment, useCallback, useEffect, useState } from "react"
import { InputSelect } from "./components/InputSelect"
import { Instructions } from "./components/Instructions"
import { Transactions } from "./components/Transactions"
import { useEmployees } from "./hooks/useEmployees"
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions"
import { useTransactionsByEmployee } from "./hooks/useTransactionsByEmployee"
import { EMPTY_EMPLOYEE } from "./utils/constants"
import { Employee, Transaction } from "./utils/types"

export function App() {
  const { data: employees, fetchAll: fetchEmployees, loading: employeesLoading } = useEmployees()
  const {
    data: paginatedTransactions,
    fetchAll: fetchPaginatedTransactions,
    invalidateData: invalidatePaginatedTransactions,
  } = usePaginatedTransactions()
  const {
    data: transactionsByEmployee,
    fetchById: fetchTransactionsByEmployee,
    invalidateData: invalidateTransactionsByEmployee,
  } = useTransactionsByEmployee()

  const [isLoading, setIsLoading] = useState(false)
  const [isAllEmployees, setIsAllEmployees] = useState(true)
  const [transactions, setTransactions] = useState<Transaction[]>([])

  // Merge transactions when data changes
  useEffect(() => {
    if (isAllEmployees && paginatedTransactions?.data) {
      setTransactions((prevTransactions) =>
        mergeTransactions(prevTransactions, paginatedTransactions.data)
      )
    } else if (!isAllEmployees && transactionsByEmployee) {
      setTransactions((prevTransactions) => mergeTransactions(prevTransactions, transactionsByEmployee))
    }
  }, [isAllEmployees, paginatedTransactions, transactionsByEmployee])

  function mergeTransactions(
    prevTransactions: Transaction[],
    newTransactions: Transaction[]
  ): Transaction[] {
    const transactionsMap = new Map<string, Transaction>()

    // Add previous transactions to the map
    prevTransactions.forEach((transaction) => {
      transactionsMap.set(transaction.id, transaction)
    })

    // Add new transactions to the map
    newTransactions.forEach((transaction) => {
      const existingTransaction = transactionsMap.get(transaction.id)
      if (existingTransaction) {
        // Preserve the approved status from the existing transaction
        transactionsMap.set(transaction.id, {
          ...transaction,
          approved: existingTransaction.approved,
        })
      } else {
        transactionsMap.set(transaction.id, transaction)
      }
    })

    // Convert map back to array
    return Array.from(transactionsMap.values())
  }

  const updateTransaction = useCallback(
    (transactionId: string, approved: boolean) => {
      setTransactions((prevTransactions) =>
        prevTransactions.map((transaction) =>
          transaction.id === transactionId ? { ...transaction, approved } : transaction
        )
      )
    },
    [setTransactions]
  )

  const loadAllTransactions = useCallback(async () => {
    setIsLoading(true)
    console.log("Loading all transactions...")
    await fetchEmployees()
    await fetchPaginatedTransactions()
    invalidateTransactionsByEmployee()
    setIsLoading(false)
  }, [fetchEmployees, fetchPaginatedTransactions, invalidateTransactionsByEmployee])

  const loadTransactionsByEmployee = useCallback(
    async (employeeId: string) => {
      setIsLoading(true)
      console.log(`Loading transactions for employee ID: ${employeeId}`)
      await fetchTransactionsByEmployee(employeeId)
      invalidatePaginatedTransactions()
      setIsLoading(false)
    },
    [fetchTransactionsByEmployee, invalidatePaginatedTransactions]
  )

  useEffect(() => {
    const initialize = async () => {
      if (!employees && !employeesLoading) {
        await loadAllTransactions()
      }
    }
    initialize()
  }, [employees, employeesLoading, loadAllTransactions])

  const handleEmployeeChange = useCallback(
    async (newValue: Employee | null) => {
      console.log("handleEmployeeChange called with:", newValue)
      if (!newValue || newValue.id === "all-employees") {
        console.log("All Employees selected.")
        setIsAllEmployees(true)
        await loadAllTransactions()
      } else {
        console.log(`Fetching transactions for employee ID: ${newValue.id}`)
        setIsAllEmployees(false)
        await loadTransactionsByEmployee(newValue.id)
      }
    },
    [loadAllTransactions, loadTransactionsByEmployee]
  )

  return (
    <Fragment>
      <main className="MainContainer">
        <Instructions />
        <hr className="RampBreak--l" />
        <InputSelect<Employee>
          isLoading={isLoading || employeesLoading}
          defaultValue={EMPTY_EMPLOYEE}
          items={employees ? [EMPTY_EMPLOYEE, ...employees] : []}
          label="Filter by employee"
          loadingLabel="Loading employees..."
          parseItem={(item) => ({
            value: item.id,
            label: `${item.firstName} ${item.lastName}`,
          })}
          onChange={handleEmployeeChange}
        />
        <div className="RampBreak--l" />
        <div className="RampGrid">
          {isLoading ? (
            <div>Loading transactions...</div>
          ) : (
            <Transactions transactions={transactions} updateTransaction={updateTransaction} />
          )}
          {isAllEmployees && paginatedTransactions?.nextPage && (
            <button
              className="RampButton"
              onClick={async () => {
                console.log("Loading more transactions...")
                await fetchPaginatedTransactions()
              }}
              disabled={isLoading}
            >
              View More
            </button>
          )}
        </div>
      </main>
    </Fragment>
  )
}
