import { useCallback, useState } from "react"
import { PaginatedRequestParams, PaginatedResponse, Transaction } from "../utils/types"
import { useCustomFetch } from "./useCustomFetch"

export function usePaginatedTransactions(): {
  data: PaginatedResponse<Transaction[]> | null
  loading: boolean
  fetchAll: () => Promise<void>
  invalidateData: () => void
} {
  const { fetchWithCache, loading } = useCustomFetch()
  const [paginatedTransactions, setPaginatedTransactions] = useState<PaginatedResponse<
    Transaction[]
  > | null>(null)

  const fetchAll = useCallback(async () => {
    const params: PaginatedRequestParams = {
      page: paginatedTransactions ? paginatedTransactions.nextPage ?? 0 : 0, // Check for null before accessing nextPage
    }

    console.log("Fetching paginated transactions with params:", params)

    // Ensure to handle the case where `nextPage` is null
    if (params.page === null) {
      console.warn("No more pages to fetch.")
      return // Early return if there's no next page to fetch
    }

    const response = await fetchWithCache<PaginatedResponse<Transaction[]>, PaginatedRequestParams>(
      "paginatedTransactions",
      params
    )

    console.log("API Response for paginated transactions:", response)

    // Update state only if we received a valid response
    if (response) {
      setPaginatedTransactions((previousResponse) => {
        if (!response.data || !Array.isArray(response.data)) {
          console.warn("Received unexpected response structure:", response)
          return previousResponse // Return the previous state if structure is invalid
        }

        return {
          data: [...(previousResponse?.data || []), ...response.data],
          nextPage: response.nextPage !== undefined ? response.nextPage : null, // Ensure nextPage is set correctly
        }
      })
    } else {
      console.warn("No response received. Returning previous state.")
    }
  }, [fetchWithCache, paginatedTransactions]) // Consider removing paginatedTransactions to avoid infinite re-fetching

  const invalidateData = useCallback(() => {
    setPaginatedTransactions(null)
  }, [])

  return { data: paginatedTransactions, loading, fetchAll, invalidateData }
}
