import { Fragment, useCallback, useEffect, useState } from "react"
import { InputSelect } from "./components/InputSelect"
import { Instructions } from "./components/Instructions"
import { Transactions } from "./components/Transactions"
import { useEmployees } from "./hooks/useEmployees"
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions"
import { useTransactionsByEmployee } from "./hooks/useTransactionsByEmployee"
import { EMPTY_EMPLOYEE } from "./utils/constants"
import { Employee } from "./utils/types"

export function App() {
  const { data: employees, fetchAll: fetchEmployees, loading: employeesLoading } = useEmployees()
  const {
    data: paginatedTransactions,
    fetchAll: fetchPaginatedTransactions,
    loading: paginatedLoading,
  } = usePaginatedTransactions()
  const {
    data: transactionsByEmployee,
    fetchById: fetchTransactionsByEmployee,
    invalidateData,
  } = useTransactionsByEmployee()

  const [isLoading, setIsLoading] = useState(false)
  const [isAllEmployees, setIsAllEmployees] = useState(true)
  const [activeTransactions, setActiveTransactions] = useState<any[]>([])

  const handleEmployeeChange = useCallback(
    async (newEmployee: Employee | null) => {
      setIsLoading(true) // Start loading indicator

      if (!newEmployee || newEmployee.id === "all-employees") {
        console.log("Switching to All Employees.")
        setIsAllEmployees(true)
        invalidateData() // Clear any previous employee-specific transactions

        // Fetch paginated transactions for all employees
        await fetchPaginatedTransactions()

        // Update active transactions with paginated data
        setActiveTransactions(paginatedTransactions?.data || [])
      } else {
        console.log(`Fetching transactions for employee ID: ${newEmployee.id}`)
        setIsAllEmployees(false)

        // Fetch transactions for the selected employee
        await fetchTransactionsByEmployee(newEmployee.id)

        // Update active transactions with employee-specific data
        setActiveTransactions(transactionsByEmployee || [])
      }

      setIsLoading(false) // Stop loading indicator
    },
    [
      fetchPaginatedTransactions,
      fetchTransactionsByEmployee,
      invalidateData,
      paginatedTransactions,
      transactionsByEmployee,
    ]
  )

  // Trigger transaction update whenever the mode or data changes
  useEffect(() => {
    if (isAllEmployees && paginatedTransactions?.data) {
      console.log("Setting active transactions to paginated data:", paginatedTransactions.data)
      setActiveTransactions(paginatedTransactions.data)
    } else if (!isAllEmployees && transactionsByEmployee) {
      console.log("Setting active transactions to employee-specific data:", transactionsByEmployee)
      setActiveTransactions(transactionsByEmployee)
    }
  }, [isAllEmployees, paginatedTransactions?.data, transactionsByEmployee]) // Added paginatedTransactions?.data here

  // Fetch employees and transactions on component mount
  useEffect(() => {
    const initialize = async () => {
      if (!employeesLoading && !employees) {
        await fetchEmployees()
      }

      if (employees) {
        await fetchPaginatedTransactions() // Load transactions for "All Employees"
        setActiveTransactions(paginatedTransactions?.data || []) // Set active transactions
      }
    }

    initialize()
  }, [employees, employeesLoading, fetchEmployees, fetchPaginatedTransactions])

  return (
    <Fragment>
      <main className="MainContainer">
        <Instructions />
        <hr className="RampBreak--l" />
        <InputSelect<Employee>
          isLoading={isLoading || employeesLoading}
          defaultValue={EMPTY_EMPLOYEE}
          items={employees ? [EMPTY_EMPLOYEE, ...employees] : []}
          label="Filter by employee"
          loadingLabel="Loading employees..."
          parseItem={(item) => ({
            value: item.id,
            label: `${item.firstName} ${item.lastName}`,
          })}
          onChange={handleEmployeeChange}
        />
        <div className="RampBreak--l" />
        <div className="RampGrid">
          {isLoading ? (
            <div>Loading transactions...</div>
          ) : (
            <Transactions transactions={activeTransactions} />
          )}
          {isAllEmployees && paginatedTransactions?.nextPage && (
            <button
              className="RampButton"
              disabled={isLoading || paginatedLoading}
              onClick={async () => {
                await fetchPaginatedTransactions()
                // After fetching more, update active transactions as needed
                setActiveTransactions(paginatedTransactions?.data || [])
              }}
            >
              View More
            </button>
          )}
        </div>
      </main>
    </Fragment>
  )
}
