import { Fragment, useCallback, useEffect, useState } from "react"
import { InputSelect } from "./components/InputSelect"
import { Instructions } from "./components/Instructions"
import { Transactions } from "./components/Transactions"
import { useEmployees } from "./hooks/useEmployees"
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions"
import { useTransactionsByEmployee } from "./hooks/useTransactionsByEmployee"
import { EMPTY_EMPLOYEE } from "./utils/constants"
import { Employee } from "./utils/types"

export function App() {
  const { data: employees, fetchAll: fetchEmployees, loading: employeesLoading } = useEmployees()
  const {
    data: paginatedTransactions,
    fetchAll: fetchPaginatedTransactions,
    loading: paginatedLoading,
  } = usePaginatedTransactions()
  const { data: transactionsByEmployee, fetchById: fetchTransactionsByEmployee } =
    useTransactionsByEmployee()

  const [isLoading, setIsLoading] = useState(false)
  const [isAllEmployees, setIsAllEmployees] = useState(true)
  const [activeTransactions, setActiveTransactions] = useState<any[]>([])

  // Fetch and display transactions for "All Employees"
  const fetchAllEmployeesTransactions = useCallback(async () => {
    setIsLoading(true)
    console.log("Fetching all employees transactions...")
    const result = await fetchPaginatedTransactions() // Fetch paginated transactions
    setActiveTransactions(result?.data || []) // Update state with transactions for "All Employees"
    setIsAllEmployees(true) // Set the view mode to "All Employees"
    setIsLoading(false)
  }, [fetchPaginatedTransactions])

  // Fetch and display transactions for a specific employee
  const fetchSpecificEmployeeTransactions = useCallback(
    async (employeeId: string) => {
      setIsLoading(true)
      console.log("Fetching transactions for employee ID:", employeeId)
      const result = await fetchTransactionsByEmployee(employeeId) // Fetch transactions by employee ID
      setActiveTransactions(result || []) // Update state with specific employee's transactions
      setIsAllEmployees(false) // Set the view mode to specific employee
      setIsLoading(false)
    },
    [fetchTransactionsByEmployee]
  )

  // Handle employee selection change
  const handleEmployeeChange = useCallback(
    async (newEmployee: Employee | null) => {
      if (!newEmployee || newEmployee.id === "all-employees") {
        console.log("Switching to All Employees view...")
        await fetchAllEmployeesTransactions() // Fetch and show transactions for "All Employees"
      } else {
        console.log("Switching to specific employee view:", newEmployee.firstName, newEmployee.lastName)
        await fetchSpecificEmployeeTransactions(newEmployee.id) // Fetch and show transactions for selected employee
      }
    },
    [fetchAllEmployeesTransactions, fetchSpecificEmployeeTransactions]
  )

  // Initial loading of employees and transactions for "All Employees"
  useEffect(() => {
    const initialize = async () => {
      if (!employeesLoading && !employees) {
        console.log("Fetching employees...")
        await fetchEmployees()
      }

      if (employees) {
        console.log("Fetching initial transactions for All Employees...")
        await fetchAllEmployeesTransactions() // Initial load of "All Employees" transactions
      }
    }

    initialize()
  }, [employees, employeesLoading, fetchEmployees, fetchAllEmployeesTransactions])

  return (
    <Fragment>
      <main className="MainContainer">
        <Instructions />
        <hr className="RampBreak--l" />

        <InputSelect<Employee>
          isLoading={isLoading || employeesLoading}
          defaultValue={EMPTY_EMPLOYEE}
          items={employees ? [EMPTY_EMPLOYEE, ...employees] : []}
          label="Filter by employee"
          loadingLabel="Loading employees..."
          parseItem={(item) => ({
            value: item.id,
            label: `${item.firstName} ${item.lastName}`,
          })}
          onChange={handleEmployeeChange} // Handle the selection change
        />

        <div className="RampBreak--l" />

        <div className="RampGrid">
          {isLoading ? (
            <div>Loading transactions...</div>
          ) : (
            <Transactions transactions={activeTransactions} /> // Render the transactions
          )}

          {isAllEmployees && paginatedTransactions?.nextPage && (
            <button
              className="RampButton"
              onClick={fetchAllEmployeesTransactions} // Fetch more transactions for "All Employees"
              disabled={isLoading || paginatedLoading || paginatedTransactions?.nextPage === null}
            >
              View More
            </button>
          )}
        </div>
      </main>
    </Fragment>
  )
}
