import { Transaction } from "../utils/types"
import { useCustomFetch } from "./useCustomFetch"

// This ensures the correct type is returned for transactions by employee
export function useTransactionsByEmployee() {
  const { fetchWithCache, loading } = useCustomFetch()
  const [transactionsByEmployee, setTransactionsByEmployee] = useState<Transaction[] | null>(null)

  const fetchById = useCallback(
    async (employeeId: string): Promise<Transaction[] | null> => {
      const response = await fetchWithCache<Transaction[]>("transactionsByEmployee", { employeeId })
      setTransactionsByEmployee(response || null) // Safely handle null responses
      return response
    },
    [fetchWithCache]
  )

  return { data: transactionsByEmployee, loading, fetchById }
}
