import { Fragment, useCallback, useEffect, useState } from "react"
import { InputSelect } from "./components/InputSelect"
import { Instructions } from "./components/Instructions"
import { Transactions } from "./components/Transactions"
import { useEmployees } from "./hooks/useEmployees"
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions"
import { useTransactionsByEmployee } from "./hooks/useTransactionsByEmployee"
import { EMPTY_EMPLOYEE } from "./utils/constants"
import { Employee } from "./utils/types"

export function App() {
  const { data: employees, fetchAll: fetchEmployees, loading: employeesLoading } = useEmployees()
  const {
    data: paginatedTransactions,
    fetchAll: fetchPaginatedTransactions,
    loading: paginatedLoading,
  } = usePaginatedTransactions()
  const {
    data: transactionsByEmployee,
    fetchById: fetchTransactionsByEmployee,
    invalidateData,
  } = useTransactionsByEmployee()

  const [isLoading, setIsLoading] = useState(false)
  const [isAllEmployees, setIsAllEmployees] = useState(true)
  const [transactionsInitialized, setTransactionsInitialized] = useState(false)
  const [activeTransactions, setActiveTransactions] = useState<any[]>([])

  // Determine which transactions to display (All Employees vs Specific Employee)
  const handleSetTransactions = useCallback(() => {
    if (isAllEmployees) {
      if (paginatedTransactions?.data) {
        console.log("Setting active transactions to paginated data:", paginatedTransactions.data)
        setActiveTransactions(paginatedTransactions.data)
      }
    } else {
      if (transactionsByEmployee) {
        console.log("Setting active transactions to employee-specific data:", transactionsByEmployee)
        setActiveTransactions(transactionsByEmployee)
      }
    }
  }, [isAllEmployees, paginatedTransactions?.data, transactionsByEmployee])

  // Fetch paginated transactions for "All Employees"
  const fetchAllEmployeesTransactions = useCallback(async () => {
    setIsLoading(true)
    console.log("Fetching paginated transactions for all employees...")
    await fetchPaginatedTransactions() // Fetch paginated transactions for all employees
    setIsAllEmployees(true)
    setActiveTransactions(paginatedTransactions?.data || []) // Update active transactions with paginated data
    setIsLoading(false)
  }, [fetchPaginatedTransactions, paginatedTransactions])

  // Fetch transactions for a specific employee
  const fetchSpecificEmployeeTransactions = useCallback(
    async (employeeId: string) => {
      setIsLoading(true)
      console.log(`Fetching transactions for employee ID: ${employeeId}`)
      await fetchTransactionsByEmployee(employeeId) // Fetch transactions for the selected employee
      setIsAllEmployees(false)
      setActiveTransactions(transactionsByEmployee || []) // Update active transactions with employee-specific data
      setIsLoading(false)
    },
    [fetchTransactionsByEmployee, transactionsByEmployee]
  )

  // Initialize employees and fetch "All Employees" transactions on first load
  useEffect(() => {
    const initialize = async () => {
      if (!employeesLoading && !employees) {
        await fetchEmployees()
      }

      if (employees && !transactionsInitialized) {
        setTransactionsInitialized(true)
        await fetchAllEmployeesTransactions() // Load transactions for "All Employees"
      }
    }

    initialize()
  }, [
    employees,
    employeesLoading,
    transactionsInitialized,
    fetchEmployees,
    fetchAllEmployeesTransactions,
  ])

  // Handle employee selection changes
  const handleEmployeeChange = useCallback(
    async (newEmployee: Employee | null) => {
      if (!newEmployee || newEmployee.id === "all-employees") {
        console.log("Switching to All Employees. Fetching paginated transactions for all employees...")
        setIsLoading(true)
        setIsAllEmployees(true)
        setActiveTransactions([]) // Clear current transactions

        // Invalidate any employee-specific transactions
        invalidateData() // Clear employee-specific transactions

        // Fetch paginated transactions for "All Employees"
        await fetchPaginatedTransactions()

        // Update active transactions with the new paginated data
        setActiveTransactions(paginatedTransactions?.data || [])

        setIsLoading(false)
      } else if (newEmployee.id) {
        console.log(`Fetching transactions for employee ID: ${newEmployee.id}`)
        setIsLoading(true)

        // Fetch transactions for the specific employee
        await fetchTransactionsByEmployee(newEmployee.id)
        setIsAllEmployees(false)
        setActiveTransactions(transactionsByEmployee || [])

        setIsLoading(false)
      }
    },
    [
      fetchPaginatedTransactions,
      fetchTransactionsByEmployee,
      paginatedTransactions,
      transactionsByEmployee,
      invalidateData,
    ]
  )

  // Handle "View More" button click
  const handleViewMore = useCallback(async () => {
    if (isAllEmployees && paginatedTransactions?.nextPage !== null) {
      await fetchPaginatedTransactions()
      handleSetTransactions() // Update active transactions after loading more data
    }
  }, [
    isAllEmployees,
    paginatedTransactions?.nextPage,
    fetchPaginatedTransactions,
    handleSetTransactions,
  ])

  // Trigger transaction update whenever the mode or data changes
  useEffect(() => {
    handleSetTransactions()
  }, [handleSetTransactions])

  return (
    <Fragment>
      <main className="MainContainer">
        <Instructions />
        <hr className="RampBreak--l" />

        <InputSelect<Employee>
          isLoading={isLoading || employeesLoading}
          defaultValue={EMPTY_EMPLOYEE}
          items={employees ? [EMPTY_EMPLOYEE, ...employees] : []}
          label="Filter by employee"
          loadingLabel="Loading employees..."
          parseItem={(item) => ({
            value: item.id,
            label: `${item.firstName} ${item.lastName}`,
          })}
          onChange={handleEmployeeChange}
        />

        <div className="RampBreak--l" />

        <div className="RampGrid">
          {isLoading ? (
            <div>Loading transactions...</div>
          ) : (
            <Transactions transactions={activeTransactions} />
          )}
          {isAllEmployees && paginatedTransactions?.nextPage && (
            <button
              className="RampButton"
              onClick={handleViewMore}
              disabled={isLoading || paginatedLoading || paginatedTransactions?.nextPage === null}
            >
              View More
            </button>
          )}
        </div>
      </main>
    </Fragment>
  )
}
